@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix t: <http://www.w3.org/2006/time#> .
@prefix ex: <http://example.org/> .
@prefix icm: <http://tio.models.tmforum.org/tio/v3.6.0/IntentCommonModel/> .
@prefix imo: <http://tio.models.tmforum.org/tio/v3.6.0/IntentManagmentOntology/> .
@prefix log: <http://tio.models.tmforum.org/tio/v3.6.0/LogicalOperators/> .
@prefix quan: <http://tio.models.tmforum.org/tio/v3.6.0/QuantityOntology/> .
@prefix set: <http://tio.models.tmforum.org/tio/v3.6.0/SetOperators/> .
@prefix fun: <http://tio.models.tmforum.org/tio/v3.6.0/FunctionOntology/> .

ex:DataCenterRedundancyIntent
	a icm:Intent ;
	log:allOf (ex:PrimaryPathExpectation
			   ex:BackupPathExpectation
			   ex:FailoverExpectation) .

ex:PrimaryPathExpectation
	a icm:DeliveryExpectation ;
	icm:target ex:PrimaryPathTarget ;
	log:allOf (ex:PrimaryBandwidthCondition
			   ex:PrimaryRouteCondition
			   ex:MPLSTunnelCondition) .

ex:BackupPathExpectation
	a icm:DeliveryExpectation ;
	icm:target ex:BackupPathTarget ;
	log:allOf (ex:BackupBandwidthCondition
			   ex:BackupRouteCondition
			   ex:BackupMPLSTunnelCondition) .

ex:FailoverExpectation
	a icm:DeliveryExpectation ;
	icm:target ex:RedundancySystemTarget ;
	log:allOf (ex:FailoverTimeCondition) .

ex:PrimaryPathTarget
	a icm:Target ;
	rdfs:member ex:DataCenterQRPrimaryPath .

ex:BackupPathTarget
	a icm:Target ;
	rdfs:member ex:DataCenterQRBackupPath .

ex:RedundancySystemTarget
	a icm:Target ;
	rdfs:member ex:DataCenterQRPrimaryPath, ex:DataCenterQRBackupPath .

ex:PrimaryRouteCondition
	a log:Condition ;
	log:matchStatement ([ rdf:subject ex:DataCenterQRPrimaryPath ;
						  rdf:predicate ex:routesVia ;
						  rdf:object ex:RouterR17 ]) .

ex:BackupRouteCondition
	a log:Condition ;
	log:matchStatement ([ rdf:subject ex:DataCenterQRBackupPath ;
						  rdf:predicate ex:routesVia ;
						  rdf:object ex:RouterR18 ]) .

ex:MPLSTunnelCondition
	a log:Condition ;
	log:matchStatement ([ rdf:subject ex:DataCenterQRPrimaryPath ;
						  rdf:predicate ex:usesProtocol ;
						  rdf:object ex:MPLS ]) .

ex:BackupMPLSTunnelCondition
	a log:Condition ;
	log:matchStatement ([ rdf:subject ex:DataCenterQRBackupPath ;
						  rdf:predicate ex:usesProtocol ;
						  rdf:object ex:MPLS ]) .

ex:PrimaryBandwidthCondition
	a log:Condition ;
	quan:atLeast (ex:Bandwidth
				  [ rdf:value "700"^^xsd:decimal ;
				    quan:unit "Mbps" ]) .

ex:BackupBandwidthCondition
	a log:Condition ;
	quan:atLeast (ex:ReservedBandwidth
				  [ rdf:value "1.4"^^xsd:decimal ;
				    quan:unit "Gbps" ]) .

ex:FailoverTimeCondition
  a log:Condition ;
  quan:atMost (ex:FailoverTime
               [ rdf:value "60"^^xsd:decimal ;
                 quan:unit "ms" ]) .
